import React, { useEffect, useRef, useState } from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import { useHistory } from 'react-router-dom';
import Alert from '@material-ui/lab/Alert';
import { FormControl, FormHelperText, InputLabel, MenuItem, Select } from '@material-ui/core';
import { useAuth } from "../context/AuthContext"
import {db} from '../../firebase'
import LogIn from '../LogIn/LogIn';
import validator from 'validator'

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright © '}
      <Link color="inherit" href="">
        Arizon Systems Pvt Ltd.
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor:"#ff7a00",
  },
  form: {
    width: '75%', // Fix IE 11 issue.
    marginTop: theme.spacing(3),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
    backgroundImage: 'linear-gradient(to left bottom, #fa630f, #fc8218, #fd9d29, #feb63f, #ffce59)',
    color: "white",
    borderRadius: '20px',
  },
}));

export default function AddUser() {
  const emailRef = useRef()
  const passwordRef= useRef()
  const passwordConfirmRef = useRef()
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('')
  const [username, setUsername] = useState('')
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('')
  const [role, setRole] = useState('')
  const [error, setError] = useState('')
  const [password, setPassword] = useState('')
  const [confirmPass, setConfirmPass] = useState('')
  const [loading, setLoading] = useState();
  const classes = useStyles();
  const {currentUser} = useAuth()
  const [admin, setAdmin] = useState(false)
  const history = useHistory()
  const [url,setUrl] = useState("")
  const { signup } = useAuth()

  useEffect(() => {
    if(currentUser.email === 'admin@gmail.com'){
      setAdmin(true)
    }
  })
  const handleReturn = () => {
      history.push('/users')
  }
   async function handleSubmit(e) {
    e.preventDefault()
    if (password !== confirmPass) {
      return setError("Passwords do not match")
    }
    if (password.length < 6){
      return setError("Weak Password ! Passwords should be atleast 6 characters")
    }
   if (phone.length < 10 || phone.length > 10 ){
    return setError("Phone Number should be 10 digits !")
   }
    if(role==='Admin'){
      setAdmin(true)
    }
   if(validator.isEmail(email)){
     setError("")
   }else{
     setError("Please put a valid email !")
   }
    
    const userData = {firstName, lastName, email, password, phone, role, username, admin, url}
    try {
      setError("")
      setLoading(true)
      await signup(email, password)
      db.collection('users').add(userData).then(() => {
          history.push('/')
          window.location.reload()
      })
    
    } catch {
      setError("Failed to create an account.This is either due to invalid input of email/ the email already exist in Database")
    }

    setLoading(false)
  }

  return (
    <>
    
    <Container component="main" >
      <div className={classes.paper}>
        <Alert severity="info">You are currently adding a new authenticated user!</Alert>
        <Avatar className={classes.avatar}>
          
        </Avatar>
        <Typography component="h1" variant="h4">
          Create User
        </Typography>
        {error && <Alert severity="error">{error}</Alert>}
        <form className={classes.form} onSubmit={handleSubmit}>
          <Grid container spacing={2}>
            <Grid item xs={12} sm={6}>
              <FormControl
               required
               fullWidth
               
               error={firstName.length > 20}
              >
                  <TextField
              value={firstName}
              error={firstName.length > 20}
                autoComplete="fname"
                name="firstName"
               required
               variant="outlined"
                id="firstName"
                label="First Name"
                autoFocus
                onChange={(e) => setFirstName(e.target.value)}
              />
              <FormHelperText>length should be less than 20</FormHelperText>
              </FormControl>
              
            </Grid>
            <Grid item xs={12} sm={6}>
            <FormControl
             required
             fullWidth
             variant="outlined"
             error={lastName.length > 20}
            >
               <TextField
              value={lastName}
              required
              error={lastName.length > 20}
              variant="outlined"
                id="lastName"
                label="Last Name"
                name="lastName"
                autoComplete="lname"
                onChange={(e) => setLastName(e.target.value)}
              />  
                <FormHelperText>length should be less than 20</FormHelperText>
            </FormControl>
             
            </Grid>
            <Grid item xs={12}>
            <FormControl
             required
             fullWidth
             variant="outlined"
            >
                <TextField
              value={email}
              required
              variant="outlined"
                id="email"
                ref={emailRef}
                label="Email Address"
                name="email"
                autoComplete="email"
                onChange={(e) => setEmail(e.target.value)}
              />
                </FormControl>
              
            </Grid>
             <Grid item xs={12}>
             <FormControl
              required
              fullWidth
              variant="outlined"
              error={username.length > 20 || username.length < 6}
             >
                 <TextField
              value={username}
              required
              error={username.length > 20 || username.length < 6}
              variant="outlined"
                label="Username"
                onChange={(e) => setUsername(e.target.value)}
              />
              <FormHelperText>Username should be atleast 6 characters and max 20 characters</FormHelperText>
                </FormControl>
             
            </Grid>
            <Grid item xs={12}>
            <FormControl
             required
             fullWidth
             variant="outlined">
                <TextField
              value={phone}
              variant="outlined"
              required
                name="password"
                label="Phone Number"
                type="Number"
                id="phone"
                autoComplete="current-password"
                onChange={(e) => setPhone(e.target.value)}
              />
              <FormHelperText>Length should be 10 characters</FormHelperText>
            </FormControl>
              
            </Grid>
            <Grid item xs={12}>
            <FormControl
             required
             fullWidth
             variant="outlined">
                 <TextField
              value={password}
              variant="outlined"
              required
                helperText="Password must have atleast 6 characters"
                ref={passwordRef}
                id="password"
                label="Password"
                name="Password"
                type="password"
                onChange={(e) => setPassword(e.target.value)}
              />
            </FormControl>
             
            </Grid>
            <Grid item xs={12}>
            <FormControl
             required
             fullWidth
             variant="outlined"
            >
                <TextField
              value={confirmPass}
              required
              variant="outlined"
                ref={passwordConfirmRef}
                name="password"
                label="Re-enter Password"
                type="password"
                id="password"
                autoComplete="current-password"
                onChange={(e) => setConfirmPass(e.target.value)}
              /> 
                </FormControl>
             
            </Grid>

            <Grid item xs={12}>
            <FormControl required className='form-select mt-1 block w-full'  fullWidth variant="outlined">
            <InputLabel id="demo-simple-select-outlined-label">Select Role</InputLabel>
                <Select
           label="Select Role"
          value={role}
         
          onChange={(e)=> setRole(e.target.value)}
          
        >
         
          <MenuItem value="Admin">Admin</MenuItem>
          <MenuItem value="Trainee">Trainee</MenuItem>
            <MenuItem value="Operator">Operator</MenuItem>
          <MenuItem value="Supervisor">Supervisor</MenuItem>
          <MenuItem value="Validator">Validator</MenuItem>
           <MenuItem value="Maintenance">Maintenance</MenuItem>
        </Select>
            </FormControl>
      
       
            </Grid>
           
          </Grid>
          {!loading && <Button
            type="submit"
            fullWidth
            variant="contained"
           
            className={classes.submit}
          >
           Create
            </Button>}
         {
           loading && <Button
            type="submit"
            fullWidth
            variant="contained"
            disabled
            className={classes.submit}
          >Creating user....</Button> 
         }   
         
        </form>
      </div>
      <Box mt={5}>
        <Copyright />
      </Box>
    </Container>
    
    </>
  );
}
